mapper.select:

SELECT * FROM restaurant RS, food F, region R 
WHERE F.food_cate=RS.rs_food_cate and R.region_cate=RS.rs_region_cate
and rs_region_cate=#{rs_region_cate}


SELECT * FROM restaurant RS, food F, region R 
WHERE F.food_cate=RS.rs_food_cate and R.region_cate=RS.rs_region_cate
and rs_food_cate=#{rs_food_cate}


SELECT * FROM restaurant RS, food F, region R 
WHERE F.food_cate=RS.rs_food_cate and R.region_cate=RS.rs_region_cate
and rs_region_cate=#{rs_region_cate} and rs_food_cate=#{rs_food_cate}

mapper.update:

			update restaurant set rs_name =#{rs_name},rs_food_cate=#{rs_food_cate},rs_phone=#{rs_phone},
      rs_region_cate=#{rs_region_cate},rs_addr=#{rs_addr}, rs_file=#{rs_file , jdbcType=VARCHAR},rs_content=#{rs_content} 
      where rs_idx= #{rs_idx}
